//custom widget tag cloud
add_filter( 'widget_tag_cloud_args', 'theme_tag_cloud_args' );
function theme_tag_cloud_args( $args ){
	$newargs = array(
		'smallest'    => 14,  //最小字号
		'largest'     => 14, //最大字号
		'unit'        => 'px',   //字号单位，可以是pt、px、em或%
		'number'      => 30,     //显示个数
		'format'      => 'flat',//列表格式，可以是flat、list或array
		'separator'   => "\n",   //分隔每一项的分隔符
		'orderby'     => 'count',//排序字段，可以是name或count
		'order'       => 'DESC', //升序或降序，ASC或DESC
		'exclude'     => null,   //结果中排除某些标签
		'include'     => null,  //结果中只包含这些标签
		'link'        => 'view', //taxonomy链接，view或edit
		'taxonomy'    => 'post_tag', //调用哪些分类法作为标签云
	);
	$return = array_merge( $args, $newargs);
	return $return;
}


/**********************************
 * http://www.2zzt.com/jcandcj/7050.html
 * 文章内图片延时加载
 **************************************/

add_filter('the_content', 'lazyload');
function lazyload($content)
{
    $loadimg_url = 'http://placehold.it/300x150&amp;text=SUMMERLEAF';
    if (!is_feed() || !is_robots) {
        $content = preg_replace('/<img(.+)src=[\'"]([^\'"]+)[\'"](.*)\/>/i', "<img\$1data-original=\"\$2\" src=\"$loadimg_url\"\$3/>\n<noscript>\$0</noscript>", $content);
    }
    return $content;
}


