//阻止站内文章Pingback 

add_action('pre_ping','leaf_noself_ping');  

function leaf_noself_ping( &$links ) {
  $home = get_option( 'home' );
  foreach ( $links as $l => $link )
  if ( 0 === strpos( $link, $home ) )
  unset($links[$l]);
}

